import React, {useState} from 'react'


 function ManipulateAccessory({id,deleteAccessory,setData,setShowForm,toggleForm,image,location,accessories,description,price}) {
  const [patch, setPatch] = useState({
    location: '',
    accessories: '',
    description: '',
    price: '',
    image: '',
  });

  function handleOnChange(event) {
    setPatch({
      ...patch,
      [event.target.name]: event.target.value,
    });
  }
  function updateAccessory(event) {
    event.preventDefault()
    console.log(event)
    fetch(`http://127.0.0.1:5555/showroom/${id}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(patch),
    })
