import React, { useEffect, useState } from 'react';
import ManipulateAccessory from './ManipulateAccessory';

const AccessoryList = ({ id }) => {
  const [data, setData] = useState([]);
  const [patch, setPatch] = useState({
    location: '',
    accessories: '',
    description: '',
    price: '',
    image: '',
  });
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    fetch('http://127.0.0.1:5555/showroom')
      .then((r) => r.json())
      .then((data) => setData(data));
  }, []);

  function deleteAccessory(id) {
    fetch(`http://127.0.0.1:5555/showroom/${id}`, {
      method: 'DELETE',
    })
      .then((resp) => resp.json())
      .then(() => {
        const updatedAccessories = data.filter((item) => item.id !== id);
        setData(updatedAccessories);
      });
  }

  function handleOnChange(event) {
    setPatch({
      ...patch,
      [event.target.name]: event.target.value,
    });
  }



  function updateAccessory(event) {
    event.preventDefault()
    console.log(event)
    fetch(`http://127.0.0.1:5555/showroom/${id}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(patch),
    })
      .then((resp) => resp.json())
      .then((updatedAccessory) => {
        const updatedData = data.map((item) =>
          item.id === updatedAccessory.id ? updatedAccessory : item
        );
        setData(updatedData);
        setShowForm(false);
      });
  }

  const toggleForm = () => {
    setShowForm(!showForm);
  };

  const accessories = data.map((item) => (
    <ManipulateAccessory
      key={item.id}
      id={item.id}
      updateAccessory={updateAccessory}
      toggleForm={toggleForm}
      deleteAccessory={deleteAccessory}
      location={item.location}
      price={item.price}
      description={item.description}
      image={item.image}
    />
  ));



  return (
    <div className="charac">
      {accessories}
      {showForm && (
        <form
          className="accessory-form"
          onSubmit={updateAccessory}
        >
          <input
            onChange={handleOnChange}
            type="text"
            name="location"
            value={patch.location}
            placeholder="location"
          />
          <input
            onChange={handleOnChange}
            type="text"
            name="accessories"
            value={patch.accessories}
            placeholder="accessory"
          />
          <input
            onChange={handleOnChange}
            type="text"
            name="description"
            value={patch.description}
            placeholder="description"
          />
          <input
            onChange={handleOnChange}
            type="text"
            name="price"
            value={patch.price}
            placeholder="price"
          />
          <input
            onChange={handleOnChange}
            type="text"
            name="image"
            value={patch.image}
            placeholder="image"
          />
          <button type="submit" className="btn" >
            Update
          </button>
        </form>
      )}
    </div>
  );
};

export default AccessoryList;
