import React, { useState } from "react";

import "./Modal.css";

export const Modal = ({ closeModal, onSubmit, defaultValue }) => {
  const [formState, setFormState] = useState(
    defaultValue || {
      location: "",
      accessories:"",
      description: "",
      price:"",
      image:"",
      status: "live",
    }
  );
  const [errors, setErrors] = useState("");

  const validateForm = () => {
    if (formState.location && formState.description && formState.status) {
      setErrors("");
      return true;
    } else {
      let errorFields = [];
      for (const [key, value] of Object.entries(formState)) {
        if (!value) {
          errorFields.push(key);
        }
      }
      setErrors(errorFields.join(", "));
      return false;
    }
  };

  const handleChange = (e) => {
    setFormState({ ...formState, [e.target.name]: e.target.value });
  };

  const handleSubmitChange = (e) => {
    e.preventDefault();

    fetch(`http://127.0.0.1:5555/showroom/${id}`,{
      method: "PATCH",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(patch)
    })
    .then(resp => resp.json())
    .then(updatedAccessory =>{
      console.log(patch)
      const update= data.map((item)=>{
        if(item.id === updatedAccessory.id) return updatedAccessory
        return item
      })
      setData(update)
    })

    onSubmit(formState);

    closeModal();
  };



  const handleSubmit = (e) => {
    e.preventDefault();

    const post = {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body:JSON.stringify(formState),
    }
    fetch('http://127.0.0.1:5555/showroom',post)
    .then(response => response.json())
    .then(data => setFormState(data))
    .catch (error => console.log (error))

    if (!validateForm()) return;

    onSubmit(formState);

    closeModal();
  };

  return (
    <div
      className="modal-container"
      onClick={(e) => {
        if (e.target.className === "modal-container") closeModal();
      }}
    >
      <div className="modal">
        <form>
          <div className="form-group">
            <label htmlFor="location">Location</label>
            <input name="location" onChange={handleChange} value={formState.location} />
          </div>
          <div className="form-group">
            <label htmlFor="accessory">Accessory</label>
            <input name="accessory" onChange={handleChange} value={formState.accessory} />
          </div>

          <div className="form-group">
            <label htmlFor="description">Description</label>
            <textarea
              name="description"
              onChange={handleChange}
              value={formState.description}
            />
          </div>
          <div className="form-group">
            <label htmlFor="price">Price</label>
            <input name="price" onChange={handleChange} value={formState.price} />
          </div>
          <div className="form-group">
            <label htmlFor="image">Image</label>
            <input name="image" onChange={handleChange} value={formState.image} />
          </div>
          {/* <div className="form-group">
            <label htmlFor="status">Status</label>
            <select
              name="status"
              onChange={handleChange}
              value={formState.status}
            >
              <option value="live">Live</option>
              <option value="draft">Draft</option>
              <option value="error">Error</option>
            </select>
          </div> */}
          {errors && <div className="error">{`Please include: ${errors}`}</div>}
          <button type="submit" className="btn" onClick={handleSubmit}>
            Submit
          </button>
        </form>
      </div>
    </div>
  );
};
