import React from 'react'


 function ManipulateAccessory({id,deleteAccessory,toggleForm,image,location,accessories,description,price}) {

  function handleOnChange(event) {
    setPatch({
      ...patch,
      [event.target.name]: event.target.value,
    });
  }
  return (
    <div className='item'>
        <p>{location}</p>
        <p>{accessories}</p>
        <p>{description}</p>
        <p>{price}</p>
        <img src={image} alt='img'className='img' />
        <button onClick={toggleForm}  className="btn">
        Update
      </button>
     {/* <button onClick={()=> updateAccessory(id)} className="btn" >update</button> */}
     <button onClick={()=> deleteAccessory(id)} className="btn" >delete</button>
     {showForm && (
        <form
          className="accessory-form"
          onSubmit={updateAccessory}
        >
          <input
            onChange={handleOnChange}
            type="text"
            name="location"
            value={patch.location}
            placeholder="location"
          />
          <input
            onChange={handleOnChange}
            type="text"
            name="accessories"
            value={patch.accessories}
            placeholder="accessory"
          />
          <input
            onChange={handleOnChange}
            type="text"
            name="description"
            value={patch.description}
            placeholder="description"
          />
          <input
            onChange={handleOnChange}
            type="text"
            name="price"
            value={patch.price}
            placeholder="price"
          />
          <input
            onChange={handleOnChange}
            type="text"
            name="image"
            value={patch.image}
            placeholder="image"
          />
          <button type="submit" className="btn" >
            Update
          </button>
        </form>
      )}
    </div>
  )
}
export default  ManipulateAccessory
